def splitall(path):
    allparts = []
    while True:
        parts = os.path.split(path)
        if parts[0] == path:
            allparts.insert(0, parts[0])
            break
        if parts[1] == path:
            allparts.insert(0, parts[1])
            break
        path = parts[0]
        allparts.insert(0, parts[1])

    return allparts


def common_substring(s1, s2):
    chunks = []
    path1 = splitall(s1)
    path2 = splitall(s2)
    for dir1, dir2 in zip(path1, path2):
        if dir1 != dir2:
            break
        chunks.append(dir1)

    return os.path.join(*chunks)


